import React, { Component } from 'react';

// import './App.css';

import { Button, Card, CardBody, CardGroup, Col, Container, Form, Input, InputGroup, InputGroupAddon, InputGroupText, Row } from 'reactstrap';
import {  Route,Switch ,HashRouter,NavLink} from 'react-router-dom';
import Reg from './registration';
import { Link } from "react-router-dom";

class Login extends Component {

    constructor() {

        super();


        this.state = {

            Email: '',

            Password: ''

        }


        this.Password = this.Password.bind(this);

        this.Email = this.Email.bind(this);

        this.login = this.login.bind(this);

    }


    Email(event) {

        this.setState({ Email: event.target.value })

    }

    Password(event) {

        this.setState({ Password: event.target.value })

    }

    login(event) {

        debugger;

        fetch('http://localhost:8000/api/peyload_app/', {

            method: 'post',

            headers: {

                'Accept': 'application/json',

                'Content-Type': 'application/json'

            },

            body: JSON.stringify({

                Email: this.state.Email,

                Password: this.state.Password

            })

        }).then((Response) => Response.json())

            .then((result) => {

                console.log(result);

                if (result.Status == 'Invalid')

                    alert('Invalid User');

                else

                alert('valid User');

            })

    }


    render() {


        return (

            <div className="app flex-row align-items-center">

                <Container>

                    <Row className="justify-content-center">

                        <Col md="9" lg="7" xl="6">


                            <CardGroup>

                                <Card className="p-2"  gradient="purple" >

                                    <CardBody>

                                        {/* <Form>

                                            <div class="row" className="mb-2 pageheading">

                                                {/* <div  class="col-sm-12 btn">

                                                    Login

                                             </div> }

                                            </div>

                                            <InputGroup className="mb-3">

                                                <Input type="text" onChange={this.Email} placeholder="Enter Email" />

                                            </InputGroup>

                                            <InputGroup className="mb-4">

                                                <Input type="password" onChange={this.Password} placeholder="Enter Password" />

                                            </InputGroup>

                                            <Button onClick={this.login} color="purple" block>Login</Button>

                                        </Form> */}


          <Form >
          <InputGroup className="mb-3">

<Input type="text" onChange={this.Email} placeholder="Enter Email" />

</InputGroup>

<InputGroup className="mb-4">

<Input type="password" onChange={this.Password} placeholder="Enter Password" />

</InputGroup>

<Button onClick={this.login} color="purple" block>Login</Button>



<HashRouter><div>
   <Link  to={{pathname: '/registration'}}>NavigateNow</Link>
   </div>

                {/* <NavLink to="/registration"><b>registerd to the  Application</b></NavLink>
                {/* <MDBBtn    color="purple" >Already registerd lets login</MDBBtn> 
                  <Switch><Route path="/registration" component={Reg} /></Switch>
                  */}

               </HashRouter>    
          </Form>

                                    </CardBody>

                                </Card>

                            </CardGroup>
                            </Col>
                            </Row>

                </Container>
</div>
        );
    }
}

export default Login;
